import React, { useState, useEffect } from "react";
import axios from "axios";

const ExcuseGenerator = () => {
  const [excuse, setExcuse] = useState("");

  const fetchExcuse = async () => {
    const response = await axios.get(
      `${process.env.REACT_APP_BACKEND_URL}/excuses`
    );
    console.log("Réponse du serveur:", response);
    const excuses = response.data;
    console.log("Excuses:", excuses); // Ajoutez cette ligne
    const randomExcuse =
      excuses[Math.floor(Math.random() * excuses.length)].message;
    console.log("Excuse générée:", randomExcuse);
    setExcuse(randomExcuse);
  };

  useEffect(() => {
    fetchExcuse();
  }, []);

  return (
    <div className="excuse-generator">
      <p>{excuse}</p>
      <button onClick={fetchExcuse}>Générer une nouvelle excuse</button>
    </div>
  );
};

export default ExcuseGenerator;
