import React, { useState, useEffect } from "react";
import axios from "axios";

const ExcuseGenerator = () => {
  const [excuse, setExcuse] = useState("");

  const fetchExcuse = async () => {
    // Remplacez cette URL par l'URL de votre serveur backend
    const response = await axios.get("http://localhost:3001/api/excuses");
    const excuses = response.data;
    setExcuse(excuses[Math.floor(Math.random() * excuses.length)].message);
  };

  useEffect(() => {
    fetchExcuse();
  }, []);

  return (
    <div className="excuse-generator">
      <p>{excuse}</p>
      <button onClick={fetchExcuse}>Générer une nouvelle excuse</button>
    </div>
  );
};

export default ExcuseGenerator;
